     1→"use client";
     2→
     3→import { useState } from "react";
     4→import { useSignIn } from "@clerk/nextjs";
     5→import { useRouter } from "next/navigation";
     6→import { isClerkAPIResponseError } from "@clerk/nextjs/errors";
     7→import { Button } from "@/components/ui/Button";
     8→import { Input } from "@/components/Input";
     9→import { Label } from "@/components/Label";
    10→import { cn } from "@/lib/utils";
    11→
    12→export function SignInForm() {
    13→  const { isLoaded, signIn, setActive } = useSignIn();
    14→  const router = useRouter();
    15→  const [email, setEmail] = useState("");
    16→  const [password, setPassword] = useState("");
    17→  const [status, setStatus] = useState<"idle" | "loading" | "success" | "error">(
    18→    "idle"
    19→  );
    20→  const [message, setMessage] = useState("");
    21→
    22→  const handleSubmit = async (e: React.FormEvent) => {
    23→    e.preventDefault();
    24→    if (!isLoaded) return;
    25→
    26→    setStatus("loading");
    27→    setMessage("");
    28→
    29→    try {
    30→      const signInAttempt = await signIn.create({
    31→        identifier: email,
    32→        password,
    33→      });
    34→
    35→      if (signInAttempt.status === "complete") {
    36→        setStatus("success");
    37→        setMessage("Signed in successfully!");
    38→        await setActive({ session: signInAttempt.createdSessionId });
    39→        router.push("/dashboard");
    40→      } else {
    41→        // Handle other statuses (needs_second_factor, etc.)
    42→        setStatus("error");
    43→        setMessage("Additional verification required. Please try again.");
    44→        console.error("Sign-in incomplete:", signInAttempt);
    45→      }
    46→    } catch (err) {
    47→      setStatus("error");
    48→      if (isClerkAPIResponseError(err)) {
    49→        // Get the first error message from Clerk
    50→        const clerkError = err.errors[0];
    51→        setMessage(clerkError?.longMessage || clerkError?.message || "Sign in failed");
    52→      } else {
    53→        setMessage("Failed to sign in. Please try again.");
    54→      }
    55→      console.error("Sign-in error:", err);
    56→    }
    57→  };
    58→
    59→  return (
    60→    <form onSubmit={handleSubmit} className="w-full max-w-md space-y-4">
    61→      <div className="space-y-2">
    62→        <Label htmlFor="signin-email">Email</Label>
    63→        <Input
    64→          id="signin-email"
    65→          type="email"
    66→          placeholder="you@example.com"
    67→          value={email}
    68→          onChange={(e) => setEmail(e.target.value)}
    69→          required
    70→          autoComplete="email"
    71→          disabled={status === "loading" || status === "success"}
    72→        />
    73→      </div>
    74→
    75→      <div className="space-y-2">
    76→        <Label htmlFor="signin-password">Password</Label>
    77→        <Input
    78→          id="signin-password"
    79→          type="password"
    80→          placeholder="Enter your password"
    81→          value={password}
    82→          onChange={(e) => setPassword(e.target.value)}
