"use client";

import Link from "next/link";
import { motion, useReducedMotion } from "framer-motion";
import { Container } from "../../ui/Container";
import { Button } from "../../ui/Button";
import { PlusCorner } from "../../ui/PlusCorner";

export function Hero() {
  const prefersReducedMotion = useReducedMotion();

  const fadeUp = (delay: number) => ({
    initial: { opacity: 0, y: 20 },
    animate: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.15,
        delay: prefersReducedMotion ? 0 : delay,
        ease: "easeOut" as const,
      },
    },
  });

  return (
    <section className="relative pt-28 pb-16 sm:pt-32 sm:pb-24 md:pt-40 md:pb-32">
      <Container>
        {/* Content wrapper with plus corners */}
        <div className="relative mx-auto max-w-3xl px-6 py-12 md:px-12 md:py-16">
          {/* Dashed border */}
          <div className="absolute inset-0 border border-dashed border-zinc-200 dark:border-zinc-800" />

          {/* Plus corners - matching FlowSection style */}
          <PlusCorner className="absolute -top-2.5 -left-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />
          <PlusCorner className="absolute -top-2.5 -right-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />
          <PlusCorner className="absolute -bottom-2.5 -left-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />
          <PlusCorner className="absolute -bottom-2.5 -right-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />

          {/* Content */}
          <div className="relative text-center">
            <motion.h1
              {...fadeUp(0)}
              className="text-3xl font-semibold tracking-tight text-black dark:text-white sm:text-4xl md:text-6xl"
            >
              Know what needs to be done.
            </motion.h1>
            <motion.p
              {...fadeUp(0.1)}
              className="mx-auto mt-2 max-w-2xl text-3xl font-semibold tracking-tight text-zinc-400 dark:text-zinc-500 sm:text-4xl md:text-6xl"
            >
              Without touching your task list.
            </motion.p>

            <motion.p
              {...fadeUp(0.2)}
              className="mx-auto mt-6 max-w-xl text-base text-zinc-500 dark:text-zinc-400 sm:mt-8 sm:text-lg md:text-xl"
            >
              AI-powered task management that extracts actionable items from your
              meetings, chats, and communications automatically.
            </motion.p>

            <motion.div {...fadeUp(0.3)} className="mt-8 sm:mt-10">
              <Link href="/sign-up">
                <Button variant="solid" size="lg">
                  Join the Waitlist
                </Button>
              </Link>
            </motion.div>
          </div>
        </div>
      </Container>
    </section>
  );
}

"use client";

import { useState } from "react";
import { PlusCornerCard } from "../ui/PlusCornerCard";
import { Icon } from "../Icon";

const dataItems = [
  { id: 1, label: "Meeting Notes", icon: "lucide:file-text" },
  { id: 2, label: "Slack Threads", icon: "lucide:message-square" },
  { id: 3, label: "Email Chains", icon: "lucide:mail" },
];

export function DataVaultCard() {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <PlusCornerCard
      className="h-full min-h-[400px] md:min-h-[440px] flex flex-col cursor-pointer"
      dashed={true}
    >
      {/* Card header */}
      <div className="p-4 md:p-6 pb-0">
        <span className="text-xs font-mono uppercase tracking-wider text-zinc-400 dark:text-zinc-500">
          Unified storage
        </span>
        <h3 className="mt-1 text-lg font-semibold text-black dark:text-white">
          Your Data Vault
        </h3>
      </div>

      {/* 3D Folder Animation */}
      <div
        className="flex-1 flex flex-col items-center justify-center p-4"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        style={{ perspective: "1000px" }}
      >
        {/* Folder visualization */}
        <div className="relative" style={{ transformStyle: "preserve-3d" }}>
          {/* Folder back panel */}
          <div
            className="w-32 h-24 bg-zinc-200 dark:bg-zinc-800 relative"
            style={{
              transformOrigin: "bottom center",
              transform: isHovered ? "rotateX(-15deg)" : "rotateX(0deg)",
              transition: "transform 500ms cubic-bezier(0.34, 1.56, 0.64, 1)",
              zIndex: 10,
            }}
          />

          {/* Security icon - always visible, pushed forward in 3D space */}
          <div
            className="absolute inset-0 flex items-center justify-center pointer-events-none"
            style={{
              transform: "translateZ(50px)",
              zIndex: 35,
            }}
          >
            <Icon
              icon="lucide:shield-check"
              size={24}
              className="text-zinc-400 dark:text-zinc-600"
            />
          </div>

          {/* Folder tab - pushed back in 3D to stay behind cards */}
          <div
            className="absolute w-12 h-4 bg-zinc-300 dark:bg-zinc-700"
            style={{
              top: "-16px",
              left: "8px",
              transformOrigin: "bottom center",
              transform: isHovered
                ? "rotateX(-25deg) translateY(-2px) translateZ(-10px)"
                : "rotateX(0deg) translateZ(-10px)",
              transition: "transform 500ms cubic-bezier(0.34, 1.56, 0.64, 1)",
              zIndex: 5,
            }}
          />

          {/* Folder front panel */}
          <div
            className="absolute top-0 left-0 w-32 h-24 bg-zinc-100 dark:bg-zinc-900"
            style={{
              transformOrigin: "bottom center",
              transform: isHovered
                ? "rotateX(25deg) translateY(8px)"
                : "rotateX(0deg)",
              transition: "transform 500ms cubic-bezier(0.34, 1.56, 0.64, 1)",
              zIndex: 30,
            }}
          />

          {/* Data cards container - behind front panel, in front of tab */}
          <div
            className="absolute"
            style={{
              top: "-20px",
              left: "50%",
              transform: "translateX(-50%)",
              zIndex: 15,
            }}
          >
            {dataItems.map((item, index) => {
              const rotations = [-12, 0, 12];
              const translations = [-40, 0, 40];
              return (
                <div
                  key={item.id}
                  className="absolute w-16 h-20 bg-background border border-zinc-300 dark:border-zinc-700 flex flex-col items-center justify-center"
                  style={{
                    transform: isHovered
                      ? `translateY(-50px) translateX(${translations[index]}px) rotate(${rotations[index]}deg) scale(1)`
                      : "translateY(20px) translateX(0) rotate(0deg) scale(0.5)",
                    opacity: isHovered ? 1 : 0,
                    transition: `all 600ms cubic-bezier(0.34, 1.56, 0.64, 1) ${index * 80}ms`,
                    zIndex: 15 - index,
                    left: "50%",
                    marginLeft: "-32px",
                  }}
                >
                  <Icon
                    icon={item.icon}
                    size={20}
                    className="text-zinc-600 dark:text-zinc-400"
                  />
                  <span className="mt-1 text-[8px] font-mono text-zinc-500 dark:text-zinc-400 text-center px-1">
                    {item.label}
                  </span>
                </div>
              );
            })}
          </div>
        </div>

        {/* Label */}
        <p className="mt-6 text-sm font-mono text-zinc-400 dark:text-zinc-500">
          Unified & indexed
        </p>
      </div>
    </PlusCornerCard>
  );
}

