"use client";

import { motion, useReducedMotion } from "framer-motion";
import { Container } from "./ui/Container";
import { Icon } from "./Icon";

interface ValuePropProps {
  icon: string;
  title: string;
  description: string;
}

function ValueProp({ icon, title, description }: ValuePropProps) {
  return (
    <div className="flex flex-col items-center text-center md:items-start md:text-left">
      <div className="mb-4 flex h-12 w-12 items-center justify-center border border-black dark:border-white">
        <Icon icon={icon} size={24} className="text-black dark:text-white" />
      </div>
      <h3 className="text-lg font-semibold text-black dark:text-white">{title}</h3>
      <p className="mt-2 text-sm leading-relaxed text-zinc-500 dark:text-zinc-400">{description}</p>
    </div>
  );
}

const containerVariants = {
  hidden: {},
  visible: {
    transition: {
      staggerChildren: 0.15,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 24 },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.12,
      ease: "easeOut" as const,
    },
  },
};

export function ValueProps() {
  const prefersReducedMotion = useReducedMotion();

  const props = [
    {
      icon: "lucide:list-checks",
      title: "Never Miss a Task",
      description:
        "Every action item from every meeting, every chat thread, every voice memo — captured automatically. Stop manually transferring notes to your task list.",
    },
    {
      icon: "lucide:link",
      title: "Context-Aware",
      description:
        "Entourage understands your projects and priorities. Tasks are organized where they belong, linked to the conversations that created them.",
    },
    {
      icon: "line-md:account",
      title: "Human in the Loop",
      description:
        "Review and approve suggested tasks before they sync. You stay in control while AI handles the busy work.",
    },
  ];

  if (prefersReducedMotion) {
    return (
      <section className="border-t border-black dark:border-white py-16 md:py-24">
        <Container>
          <div className="grid gap-12 md:grid-cols-3 md:gap-8">
            {props.map((prop, i) => (
              <ValueProp key={i} {...prop} />
            ))}
          </div>
        </Container>
      </section>
    );
  }

  return (
    <section className="border-t border-black dark:border-white py-16 md:py-24">
      <Container>
        <motion.div
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, margin: "-100px" }}
          variants={containerVariants}
          className="grid gap-12 md:grid-cols-3 md:gap-8"
        >
          {props.map((prop, i) => (
            <motion.div key={i} variants={itemVariants}>
              <ValueProp {...prop} />
            </motion.div>
          ))}
        </motion.div>
      </Container>
    </section>
  );
}

"use client";

import Link from "next/link";
import { motion, useReducedMotion } from "framer-motion";
import { Container } from "./ui/Container";
import { Button } from "./ui/Button";

export function SecondaryCTA() {
  const prefersReducedMotion = useReducedMotion();

  if (prefersReducedMotion) {
    return (
      <section className="border-t border-black dark:border-white py-16 md:py-24">
        <Container>
          <div className="mx-auto max-w-xl text-center">
            <h2 className="text-2xl font-semibold tracking-tight text-black dark:text-white md:text-3xl">
              Ready to stop missing action items?
            </h2>

            <div className="mt-8">
              <Link href="/sign-up">
                <Button variant="primary" size="lg">
                  Join the Waitlist
                </Button>
              </Link>
            </div>

            <p className="mt-4 text-sm text-zinc-500 dark:text-zinc-400">
              We&apos;re onboarding users in batches. Get early access.
            </p>
          </div>
        </Container>
      </section>
    );
  }

  return (
    <section className="border-t border-black dark:border-white py-16 md:py-24">
      <Container>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, margin: "-100px" }}
          transition={{ duration: 0.12, ease: "easeOut" }}
          className="mx-auto max-w-xl text-center"
        >
          <h2 className="text-2xl font-semibold tracking-tight text-black dark:text-white md:text-3xl">
            Ready to stop missing action items?
          </h2>

          <motion.div
            initial={{ opacity: 0, y: 10 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.12, delay: 0.05, ease: "easeOut" }}
            className="mt-8"
          >
            <Link href="/sign-up">
              <Button variant="primary" size="lg">
                Join the Waitlist
              </Button>
            </Link>
          </motion.div>

          <motion.p
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.12, delay: 0.1 }}
            className="mt-4 text-sm text-zinc-500 dark:text-zinc-400"
          >
            We&apos;re onboarding users in batches. Get early access.
          </motion.p>
        </motion.div>
      </Container>
    </section>
  );
}

