import { ButtonHTMLAttributes, ReactNode } from "react";

interface ButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  children: ReactNode;
  variant?: "primary" | "solid" | "secondary" | "ghost";
  size?: "default" | "lg";
}

export function Button({
  children,
  variant = "primary",
  size = "default",
  className = "",
  ...props
}: ButtonProps) {
  const baseStyles =
    "inline-flex items-center justify-center font-medium transition-colors cursor-pointer focus:outline-none focus:ring-2 focus:ring-black focus:ring-offset-2";

  const variants = {
    primary:
      "bg-white dark:bg-black text-black dark:text-white border border-black dark:border-white hover:bg-zinc-100 dark:hover:bg-zinc-800 rounded-none",
    solid:
      "bg-black dark:bg-white text-white dark:text-black border border-black dark:border-white hover:bg-zinc-800 dark:hover:bg-zinc-100 rounded-none",
    secondary:
      "bg-transparent text-black dark:text-white border border-black dark:border-white hover:bg-zinc-100 dark:hover:bg-zinc-800 rounded-none",
    ghost:
      "bg-transparent text-black dark:text-white hover:bg-zinc-100 dark:hover:bg-zinc-800 border border-transparent hover:border-zinc-200 dark:hover:border-zinc-700 rounded-none",
  };

  const sizes = {
    default: "h-10 px-5 text-sm",
    lg: "h-12 px-8 text-base",
  };

  return (
    <button
      className={`${baseStyles} ${variants[variant]} ${sizes[size]} ${className}`}
      {...props}
    >
      {children}
    </button>
  );
}

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";

const cardVariants = cva("w-full relative", {
  variants: {
    variant: {
      default: [
        "border",
        "border-black dark:border-white",
        "bg-white dark:bg-black",
      ],
      plus: [
        "border border-dashed",
        "border-zinc-400 dark:border-zinc-600",
        "relative",
      ],
    },
  },
  defaultVariants: {
    variant: "default",
  },
});

export interface CardProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof cardVariants> {
  title?: string;
  description?: string;
}

const CardContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("p-6", className)} {...props}>
    {props.children}
  </div>
));
CardContent.displayName = "CardContent";

const Card = React.forwardRef<HTMLDivElement, CardProps>(
  ({ className, variant, title, description, children, ...props }, ref) => {
    const PlusIcons = () => (
      <>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          width={20}
          height={20}
          strokeWidth="1"
          stroke="currentColor"
          className="text-zinc-400 dark:text-zinc-600 size-5 absolute -top-2.5 -left-2.5"
        >
          <path
            strokeLinecap="square"
            strokeLinejoin="miter"
            d="M12 6v12m6-6H6"
          />
        </svg>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          width={20}
          height={20}
          strokeWidth="1"
          stroke="currentColor"
          className="text-zinc-400 dark:text-zinc-600 size-5 absolute -top-2.5 -right-2.5"
        >
          <path
            strokeLinecap="square"
            strokeLinejoin="miter"
            d="M12 6v12m6-6H6"
          />
        </svg>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          width={20}
          height={20}
          strokeWidth="1"
          stroke="currentColor"
          className="text-zinc-400 dark:text-zinc-600 size-5 absolute -bottom-2.5 -left-2.5"
        >
          <path
            strokeLinecap="square"
            strokeLinejoin="miter"
            d="M12 6v12m6-6H6"
          />
        </svg>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          width={20}
          height={20}
          strokeWidth="1"
          stroke="currentColor"
          className="text-zinc-400 dark:text-zinc-600 size-5 absolute -bottom-2.5 -right-2.5"
        >
          <path
            strokeLinecap="square"
            strokeLinejoin="miter"
            d="M12 6v12m6-6H6"
          />
        </svg>
      </>
    );

    const content = (
      <CardContent>
        {title && (
          <h3 className="text-lg font-semibold mb-1 text-black dark:text-white">
            {title}
          </h3>
        )}
        {description && (
          <p className="text-zinc-600 dark:text-zinc-400">{description}</p>
        )}
        {children}
      </CardContent>
    );

    return (
      <div
        ref={ref}
        className={cn(cardVariants({ variant, className }))}
        {...props}
      >
        {variant === "plus" && <PlusIcons />}
        {content}
      </div>
    );
  }
);
Card.displayName = "Card";

export { Card, CardContent, cardVariants };

