"use client";

import { cn } from "@/lib/utils";

type InfiniteSliderProps = {
  children: React.ReactNode;
  gap?: number;
  duration?: number;
  direction?: "horizontal" | "vertical";
  reverse?: boolean;
  className?: string;
  speed?: number;
};

export function InfiniteSlider({
  children,
  gap = 16,
  duration = 25,
  direction = "horizontal",
  reverse = false,
  className,
  speed,
}: InfiniteSliderProps) {
  // Convert speed to duration if provided (higher speed = lower duration)
  const effectiveDuration = speed ? 100 / speed : duration;

  return (
    <div className={cn("overflow-hidden", className)}>
      <div
        className={cn(
          "flex w-max",
          direction === "horizontal" ? "flex-row" : "flex-col",
          reverse ? "animate-scroll-reverse" : "animate-scroll"
        )}
        style={{
          gap: `${gap}px`,
          animationDuration: `${effectiveDuration}s`,
        }}
      >
        {children}
        {children}
      </div>
    </div>
  );
}

"use client";

/**
 * Blueprint-style grid background inspired by Vercel font page
 * Subtle dashed grid with technical measurement annotations
 */
export function BlueprintGrid() {
  return (
    <div className="absolute inset-0 -z-10 overflow-hidden pointer-events-none">
      {/* Horizontal grid lines */}
      <div
        className="absolute inset-0"
        style={{
          backgroundImage: `
            repeating-linear-gradient(
              0deg,
              transparent,
              transparent 79px,
              var(--grid-line-color, rgba(0, 0, 0, 0.04)) 79px,
              var(--grid-line-color, rgba(0, 0, 0, 0.04)) 80px
            )
          `,
        }}
      />
      {/* Vertical grid lines */}
      <div
        className="absolute inset-0"
        style={{
          backgroundImage: `
            repeating-linear-gradient(
              90deg,
              transparent,
              transparent 79px,
              var(--grid-line-color, rgba(0, 0, 0, 0.04)) 79px,
              var(--grid-line-color, rgba(0, 0, 0, 0.04)) 80px
            )
          `,
        }}
      />
      {/* Corner coordinate annotation */}
      <div className="absolute top-4 left-4 font-mono text-[10px] text-zinc-300 select-none">
        0,0
      </div>
      {/* Right side measurement */}
      <div className="absolute top-1/2 right-4 font-mono text-[10px] text-zinc-300 -rotate-90 origin-right select-none">
        80px
      </div>
    </div>
  );
}

