/* Infinite scroll animations - GPU accelerated for smooth performance */
@keyframes scroll {
  0% {
    transform: translate3d(0, 0, 0);
  }
  100% {
    transform: translate3d(calc(-50% - var(--slider-gap, 0px) / 2), 0, 0);
  }
}

@keyframes scroll-reverse {
  0% {
    transform: translate3d(calc(-50% - var(--slider-gap, 0px) / 2), 0, 0);
  }
  100% {
    transform: translate3d(0, 0, 0);
  }
}

.animate-scroll {
  animation: scroll linear infinite;
  will-change: transform;
  backface-visibility: hidden;
  -webkit-backface-visibility: hidden;
  /* Prevent sub-pixel rendering issues */
  transform: translateZ(0);
}

.animate-scroll-reverse {
  animation: scroll-reverse linear infinite;
  will-change: transform;
  backface-visibility: hidden;
  -webkit-backface-visibility: hidden;
  /* Prevent sub-pixel rendering issues */
  transform: translateZ(0);
}

/* Respect reduced motion preferences */
@media (prefers-reduced-motion: reduce) {
  .animate-scroll,
  .animate-scroll-reverse {
    animation: none;
  }
}

/* Hero fade-up animations - CSS-based for iOS Safari compatibility */

