  --shadow-xl: var(--shadow-xl);
}

body {
  font-family: var(--font-switzer), system-ui, sans-serif;
}

/* Flow diagram animations */
@keyframes pulse-subtle {
  0%, 100% {
    opacity: 0.85;
    transform: scale(1);
  }
  50% {
    opacity: 1;
    transform: scale(1.02);
  }
}

.animate-pulse-slow {
  animation: pulse-subtle 3s ease-in-out infinite;
}

/* Circuit board signal animations */
.circuit-signal {
  offset-anchor: 8px 0px;
  animation: circuit-signal-path;
  animation-iteration-count: infinite;
  animation-timing-function: cubic-bezier(0.75, -0.01, 0, 0.99);
  animation-duration: 4s;
  animation-delay: 1.5s;
}

.circuit-signal-1 {
  offset-path: path("M 60 30 v 35 q 0 5 5 5 h 115 q 5 0 5 5 v 25");
  animation-delay: 1.5s;
  animation-duration: 3s;
}

.circuit-signal-2 {
  offset-path: path("M 140 30 v 25 q 0 5 5 5 h 35 q 5 0 5 5 v 35");
  animation-delay: 2s;
  animation-duration: 4s;
}

.circuit-signal-3 {
  offset-path: path("M 200 30 v 70");
  animation-delay: 1.8s;
  animation-duration: 3s;
}

.circuit-signal-4 {
  offset-path: path("M 260 30 v 25 q 0 5 -5 5 h -35 q -5 0 -5 5 v 35");
  animation-delay: 2.2s;
  animation-duration: 4s;
}

.circuit-signal-5 {
  offset-path: path("M 340 30 v 35 q 0 5 -5 5 h -115 q -5 0 -5 5 v 25");
  animation-delay: 1.7s;
  animation-duration: 5s;
}

.circuit-signal-6 {
  offset-path: path("M 60 170 v -35 q 0 -5 5 -5 h 115 q 5 0 5 -5 v -25");
  animation-delay: 2.5s;
  animation-duration: 4s;
}

.circuit-signal-7 {
  offset-path: path("M 140 170 v -25 q 0 -5 5 -5 h 35 q 5 0 5 -5 v -35");
  animation-delay: 2.3s;
  animation-duration: 4s;
}

.circuit-signal-8 {
  offset-path: path("M 200 170 v -50");
  animation-delay: 2s;
  animation-duration: 3s;
}

.circuit-signal-9 {
  offset-path: path("M 260 170 v -25 q 0 -5 -5 -5 h -35 q -5 0 -5 -5 v -35");
  animation-delay: 2.1s;
  animation-duration: 4s;
}

.circuit-signal-10 {
  offset-path: path("M 340 170 v -35 q 0 -5 -5 -5 h -115 q -5 0 -5 -5 v -25");
  animation-delay: 2.4s;
  animation-duration: 4s;
}

@keyframes circuit-signal-path {
  0% {
    offset-distance: 0%;
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  85% {
    opacity: 1;
  }
  100% {
    offset-distance: 100%;
    opacity: 0;
  }
}

/* FlowSection Action Badges - Smooth, subtle glow with distinct colors per badge */
/* Extract = Violet, Analyze = Sky, Organize = Emerald (per design system) */

.flow-action-badge {
  will-change: box-shadow, border-color;
}

/* Extract badge - Violet (#8B5CF6) */
.flow-action-extract {
  animation: flow-action-extract 6s ease-in-out infinite;
}

@keyframes flow-action-extract {
  0%, 100% {
    box-shadow: 0 0 0 0 transparent;
    border-color: rgb(228 228 231);
  }
  50% {
    box-shadow: 0 0 6px 0 rgba(139, 92, 246, 0.25);
    border-color: rgba(139, 92, 246, 0.5);
  }
}

/* Analyze badge - Sky (#0EA5E9) */
.flow-action-analyze {
  animation: flow-action-analyze 6s ease-in-out infinite;
}

@keyframes flow-action-analyze {
  0%, 100% {
    box-shadow: 0 0 0 0 transparent;
    border-color: rgb(228 228 231);
  }
  50% {
    box-shadow: 0 0 6px 0 rgba(14, 165, 233, 0.25);
    border-color: rgba(14, 165, 233, 0.5);
  }
}

/* Organize badge - Emerald (#10B981) */
.flow-action-organize {
  animation: flow-action-organize 6s ease-in-out infinite;
}

@keyframes flow-action-organize {
  0%, 100% {
    box-shadow: 0 0 0 0 transparent;
    border-color: rgb(228 228 231);
  }
  50% {
    box-shadow: 0 0 6px 0 rgba(16, 185, 129, 0.25);
    border-color: rgba(16, 185, 129, 0.5);
  }
}

/* Dark mode variants */
.dark .flow-action-extract {
  animation: flow-action-extract-dark 6s ease-in-out infinite;
}

@keyframes flow-action-extract-dark {
  0%, 100% {
    box-shadow: 0 0 0 0 transparent;
    border-color: rgb(63 63 70);
  }
  50% {
    box-shadow: 0 0 6px 0 rgba(139, 92, 246, 0.2);
    border-color: rgba(139, 92, 246, 0.4);
  }
}

.dark .flow-action-analyze {
  animation: flow-action-analyze-dark 6s ease-in-out infinite;
}

@keyframes flow-action-analyze-dark {
  0%, 100% {
    box-shadow: 0 0 0 0 transparent;
    border-color: rgb(63 63 70);
  }
  50% {
    box-shadow: 0 0 6px 0 rgba(14, 165, 233, 0.2);
    border-color: rgba(14, 165, 233, 0.4);
  }
}

.dark .flow-action-organize {
  animation: flow-action-organize-dark 6s ease-in-out infinite;
}

@keyframes flow-action-organize-dark {
  0%, 100% {
    box-shadow: 0 0 0 0 transparent;
    border-color: rgb(63 63 70);
  }
  50% {
    box-shadow: 0 0 6px 0 rgba(16, 185, 129, 0.2);
    border-color: rgba(16, 185, 129, 0.4);
  }
}

/* FlowSection Task Arrival Animation - GPU-optimized */
.flow-task-arrive {
  will-change: box-shadow;
  animation: flow-task-pulse 4s ease-out infinite;
}

@keyframes flow-task-pulse {
  0%, 70% {
    box-shadow: 0 0 0 0 transparent;
  }
  75% {
    box-shadow: 0 0 0 0 rgba(16, 185, 129, 0.5);
  }
  100% {
    box-shadow: 0 0 0 12px rgba(16, 185, 129, 0);
  }
}

.flow-checkmark-bounce {
  will-change: transform;
  animation: flow-checkmark-bounce 4s ease-out infinite;
}

@keyframes flow-checkmark-bounce {
  0%, 70% {
    transform: scale(1);
  }
  80% {
    transform: scale(1.25);
  }
  90% {
    transform: scale(0.95);
  }
  100% {
    transform: scale(1);
  }
}

/* FlowSection Input Source - departure pulse with randomized timing */
.flow-input-source {
  will-change: box-shadow;
  animation: flow-input-pulse 4s ease-out infinite;
}

/* Random-feeling delays using prime number ratios to avoid synchronization */
.flow-input-source:nth-child(1) { animation-delay: 0.2s; animation-duration: 3.7s; }
.flow-input-source:nth-child(2) { animation-delay: 1.1s; animation-duration: 4.3s; }
.flow-input-source:nth-child(3) { animation-delay: 0.7s; animation-duration: 3.9s; }
.flow-input-source:nth-child(4) { animation-delay: 1.5s; animation-duration: 4.1s; }

@keyframes flow-input-pulse {
  0% {
    box-shadow: 0 0 0 0 transparent;
  }
  /* Fast appear */
  2% {
    box-shadow: 0 0 0 0 rgba(245, 158, 11, 0.6);
  }
  /* Slow fade out - expand over longer duration */
  15% {
    box-shadow: 0 0 0 4px rgba(245, 158, 11, 0.3);
  }
  30% {
    box-shadow: 0 0 0 8px rgba(245, 158, 11, 0);
  }
  35%, 100% {
    box-shadow: 0 0 0 0 transparent;
  }
}

.dark .flow-input-source {
  animation: flow-input-pulse-dark 4s ease-out infinite;
}

/* Random-feeling delays preserved in dark mode */
.dark .flow-input-source:nth-child(1) { animation-delay: 0.2s; animation-duration: 3.7s; }
.dark .flow-input-source:nth-child(2) { animation-delay: 1.1s; animation-duration: 4.3s; }
.dark .flow-input-source:nth-child(3) { animation-delay: 0.7s; animation-duration: 3.9s; }
.dark .flow-input-source:nth-child(4) { animation-delay: 1.5s; animation-duration: 4.1s; }

@keyframes flow-input-pulse-dark {
  0% {
    box-shadow: 0 0 0 0 transparent;
  }
  /* Fast appear */
  2% {
    box-shadow: 0 0 0 0 rgba(245, 158, 11, 0.5);
  }
  /* Slow fade out - expand over longer duration */
  15% {
    box-shadow: 0 0 0 4px rgba(245, 158, 11, 0.25);
  }
  30% {
    box-shadow: 0 0 0 8px rgba(245, 158, 11, 0);
  }
  35%, 100% {
    box-shadow: 0 0 0 0 transparent;
  }
}

