          </div>

          {/* Logo cloud */}
          <div className="overflow-hidden pt-8 pb-6 [mask-image:linear-gradient(to_right,transparent,black_5%,black_95%,transparent)] sm:[mask-image:linear-gradient(to_right,transparent,black_10%,black_90%,transparent)]">
            <InfiniteSlider gap={64} reverse speed={5.6}>
              {credentials.map((cred) => (
                <div
                  key={cred.name}
                  className={cn(
                    "flex-shrink-0 flex items-center justify-center h-8 grayscale opacity-50 hover:opacity-100 hover:grayscale-0 transition-all duration-300",
                    !cred.logoDark && "dark:invert dark:hover:invert-0"
                  )}
                >
                  {/* Light mode logo */}
                  <Image
                    src={cred.logo}
                    alt={cred.name}
                    width={120}
                    height={32}
                    className={cn(
                      "h-6 md:h-8 w-auto object-contain",
                      cred.logoDark && "dark:hidden",
                      cred.className
                    )}
                  />
                  {/* Dark mode logo (if different) */}
                  {cred.logoDark && (
                    <Image
                      src={cred.logoDark}
                      alt={cred.name}
                      width={120}
                      height={32}
                      className={cn(
                        "h-6 md:h-8 w-auto object-contain hidden dark:block",
                        cred.className
                      )}
                    />
                  )}
                </div>
              ))}
            </InfiniteSlider>
          </div>

          {/* Bottom border with plus corners */}
          <div className="absolute bottom-0 left-0 right-0 border-t border-dashed border-zinc-200 dark:border-zinc-800" />
          <PlusCorner className="absolute -bottom-2.5 -left-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />
          <PlusCorner className="absolute -bottom-2.5 -right-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />
        </div>
      </Container>
    </section>
  );
}
