"use client";

import { useCallback, useEffect, useRef, useState } from "react";
import { cn } from "@/lib/utils";

interface RippleGridProps {
  className?: string;
}

export function RippleGrid({ className }: RippleGridProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [cells, setCells] = useState<{ row: number; col: number }[]>([]);
  const [rippleCenter, setRippleCenter] = useState<{ row: number; col: number } | null>(null);
  const [rippleKey, setRippleKey] = useState(0);

  const rows = 12;
  const cols = 24;
  const cellSize = 56;

  // Generate cells on mount
  useEffect(() => {
    const newCells: { row: number; col: number }[] = [];
    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
        newCells.push({ row, col });
      }
    }
    setCells(newCells);
  }, []);

  const handleCellClick = useCallback((row: number, col: number) => {
    setRippleCenter({ row, col });
    setRippleKey((k) => k + 1);
  }, []);

  const getDistance = (r1: number, c1: number, r2: number, c2: number) => {
    return Math.sqrt(Math.pow(r1 - r2, 2) + Math.pow(c1 - c2, 2));
  };

  return (
    <div
      ref={containerRef}
      className={cn(
        "absolute inset-0 overflow-hidden pointer-events-none",
        className
      )}
    >
      <div
        className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2"
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${cols}, ${cellSize}px)`,
          gridTemplateRows: `repeat(${rows}, ${cellSize}px)`,
        }}
      >
        {cells.map(({ row, col }) => {
          const distance = rippleCenter
            ? getDistance(row, col, rippleCenter.row, rippleCenter.col)
            : 0;
          const delay = distance * 50; // 50ms per unit distance

          return (
            <div
              key={`${row}-${col}-${rippleKey}`}
              className={cn(
                "border border-zinc-200/50 dark:border-zinc-800/50 transition-all duration-150",
                "hover:bg-zinc-100/80 dark:hover:bg-zinc-900/80",
                "hover:border-zinc-300 dark:hover:border-zinc-700",
                rippleCenter && "animate-cell-ripple dark:animate-cell-ripple-dark"
              )}
              style={{
                animationDelay: rippleCenter ? `${delay}ms` : undefined,
                pointerEvents: "auto",
              }}
              onClick={() => handleCellClick(row, col)}
            />
          );
        })}
      </div>
      {/* Radial fade mask */}
      <div
        className="absolute inset-0 bg-[radial-gradient(ellipse_at_center,transparent_0%,transparent_30%,white_70%)] dark:bg-[radial-gradient(ellipse_at_center,transparent_0%,transparent_30%,black_70%)]"
      />
    </div>
  );
}

"use client";

import Image from "next/image";
import { cn } from "@/lib/utils";
import { Container } from "./ui/Container";
import { InfiniteSlider } from "./ui/InfiniteSlider";

interface TeamCredentialsSectionProps {
  className?: string;
}

// Company and university logos
const credentials = [
  { name: "DeepMind", logo: "/logos/deepmind.svg", className: "" },
  { name: "Google", logo: "/logos/google.svg", className: "" },
  { name: "McKinsey", logo: "/logos/mckinsey.svg", className: "" },
  { name: "Boeing", logo: "/logos/boeing.svg", className: "" },
  { name: "Bain", logo: "/logos/bain.svg", className: "" },
  { name: "Harvard", logo: "/logos/harvard.svg", className: "" },
  { name: "Aalto", logo: "/logos/aalto.png", className: "scale-150" },
];

export function TeamCredentialsSection({
  className,
}: TeamCredentialsSectionProps) {
  return (
    <section className={cn("py-12 md:py-16 bg-white dark:bg-black", className)}>
      <Container>
        <div className="relative mx-auto max-w-3xl">
          {/* Header with brand styling */}
          <div className="flex items-center justify-center gap-4 mb-8">
            {/* Left dashed line with plus */}
            <div className="flex items-center gap-2 flex-1">
              <span className="text-zinc-300 dark:text-zinc-700 text-xs">+</span>
              <div className="flex-1 border-t border-dashed border-zinc-300 dark:border-zinc-700" />
            </div>

            {/* Label */}
            <span className="text-xs font-mono uppercase tracking-widest text-zinc-400 dark:text-zinc-500 whitespace-nowrap">
              Built by engineers from
            </span>

            {/* Right dashed line with plus */}
            <div className="flex items-center gap-2 flex-1">
              <div className="flex-1 border-t border-dashed border-zinc-300 dark:border-zinc-700" />
              <span className="text-zinc-300 dark:text-zinc-700 text-xs">+</span>
            </div>
          </div>

          {/* Logo cloud */}
          <div className="overflow-hidden py-6 [mask-image:linear-gradient(to_right,transparent,black_10%,black_90%,transparent)]">
            <InfiniteSlider gap={64} reverse speed={5}>
              {credentials.map((cred) => (
                <div
                  key={cred.name}
                  className="flex items-center justify-center h-8 grayscale opacity-50 hover:opacity-100 hover:grayscale-0 transition-all duration-300"
                >
                  <Image
                    src={cred.logo}
                    alt={cred.name}
                    width={120}
                    height={32}
                    className={cn(
                      "h-6 md:h-8 w-auto object-contain dark:invert",
                      cred.className
                    )}
                  />
                </div>
              ))}
            </InfiniteSlider>
          </div>

          {/* Bottom divider with plus signs */}
          <div className="flex items-center gap-2 mt-8">
            <span className="text-zinc-300 dark:text-zinc-700 text-xs">+</span>
            <div className="flex-1 border-t border-dashed border-zinc-300 dark:border-zinc-700" />
            <span className="text-zinc-300 dark:text-zinc-700 text-xs">+</span>
          </div>
        </div>
      </Container>
    </section>
  );
}

"use client";

import Link from "next/link";
import { motion, useReducedMotion } from "framer-motion";
import { Container } from "./ui/Container";
import { Button } from "./ui/Button";
import { RippleGrid } from "./RippleGrid";

export function Hero() {
  const prefersReducedMotion = useReducedMotion();

  const fadeUp = (delay: number) => ({
    initial: { opacity: 0, y: 20 },
    animate: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.15,
        delay: prefersReducedMotion ? 0 : delay,
        ease: "easeOut" as const,
      },
    },
  });

  return (
    <section className="relative pt-28 pb-16 sm:pt-32 sm:pb-24 md:pt-40 md:pb-32 overflow-hidden">
      <RippleGrid />
      <Container className="relative z-10">
        <div className="mx-auto max-w-3xl text-center">
          <motion.h1
            {...fadeUp(0)}
            className="text-3xl font-semibold tracking-tight text-black dark:text-white sm:text-4xl md:text-6xl"
          >
            Know what needs to be done.
          </motion.h1>
          <motion.p
            {...fadeUp(0.1)}
            className="mx-auto mt-2 max-w-2xl text-3xl font-semibold tracking-tight text-zinc-400 dark:text-zinc-500 sm:text-4xl md:text-6xl"
          >
            Without touching your task list.
          </motion.p>

          <motion.p
            {...fadeUp(0.2)}
            className="mx-auto mt-6 max-w-xl text-base text-zinc-500 dark:text-zinc-400 sm:mt-8 sm:text-lg md:text-xl"
          >
            AI-powered task management that extracts actionable items from your
            meetings, chats, and communications automatically.
          </motion.p>

          <motion.div {...fadeUp(0.3)} className="mt-8 sm:mt-10">
            <Link href="/sign-up">
              <Button variant="solid" size="lg">
                Join the Waitlist
              </Button>
            </Link>
          </motion.div>
        </div>
      </Container>
    </section>
  );
}

