  100% {
    background-color: transparent;
    border-color: rgba(255, 255, 255, 0.05);
  }
}

.animate-cell-ripple {
  animation: cell-ripple 600ms ease-out forwards;
}

.animate-cell-ripple-dark {
  animation: cell-ripple-dark 600ms ease-out forwards;
}

/* Circuit board signal animations */
.circuit-signal {
  offset-anchor: 8px 0px;
  animation: circuit-signal-path;
  animation-iteration-count: infinite;
  animation-timing-function: cubic-bezier(0.75, -0.01, 0, 0.99);
  animation-duration: 4s;
  animation-delay: 1.5s;
}

