              transition: "transform 500ms cubic-bezier(0.34, 1.56, 0.64, 1)",
              zIndex: 10,
            }}
          />

          {/* Data cards container */}
          <div
            className="absolute"
            style={{
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              zIndex: 20,
            }}
          >
            {dataItems.map((item, index) => {
              const rotations = [-12, 0, 12];
              const translations = [-40, 0, 40];
              return (
                <div
                  key={item.id}
                  className="absolute w-16 h-20 bg-white dark:bg-black border border-zinc-300 dark:border-zinc-700 flex flex-col items-center justify-center"
                  style={{
                    transform: isHovered
                      ? `translateY(-70px) translateX(${translations[index]}px) rotate(${rotations[index]}deg) scale(1)`
                      : "translateY(0) translateX(0) rotate(0deg) scale(0.5)",
                    opacity: isHovered ? 1 : 0,
                    transition: `all 600ms cubic-bezier(0.34, 1.56, 0.64, 1) ${index * 80}ms`,
                    zIndex: 10 - index,
                    left: "50%",
                    top: "50%",
                    marginLeft: "-32px",
                    marginTop: "-40px",
                  }}
                >
                  <Icon
                    icon={item.icon}
                    size={20}
                    className="text-zinc-600 dark:text-zinc-400"
                  />
                  <span className="mt-1 text-[8px] font-mono text-zinc-500 dark:text-zinc-400 text-center px-1">
                    {item.label}
                  </span>
                </div>
              );
            })}
          </div>

          {/* Folder front panel */}
          <div
            className="absolute top-0 left-0 w-32 h-24 bg-zinc-100 dark:bg-zinc-900"
            style={{
              transformOrigin: "bottom center",
              transform: isHovered
                ? "rotateX(25deg) translateY(8px)"
                : "rotateX(0deg)",
              transition: "transform 500ms cubic-bezier(0.34, 1.56, 0.64, 1)",
              zIndex: 30,
            }}
          />
        </div>

        {/* Labels */}
        <p
          className="mt-6 text-sm font-mono text-zinc-500 dark:text-zinc-400"
          style={{
            opacity: isHovered ? 0.7 : 1,
            transition: "opacity 300ms",
          }}
        >
          Unified & indexed
        </p>
        <p
          className="mt-2 text-xs font-mono text-zinc-400 dark:text-zinc-500"
          style={{
            opacity: isHovered ? 0 : 0.6,
            transform: isHovered ? "translateY(10px)" : "translateY(0)",
            transition: "all 300ms",
          }}
        >
          Hover to explore
        </p>
      </div>
    </PlusCornerCard>
  );
}

"use client";

import Link from "next/link";
import { useState } from "react";
import { motion, useMotionValueEvent, useScroll } from "framer-motion";
import { Button } from "./ui/Button";
import { LogoMark, LogoFull } from "./Logo";
import { ThemeToggle } from "./ThemeToggle";
import { cn } from "@/lib/utils";

export function Header() {
  const { scrollY } = useScroll();
  const [isScrolled, setIsScrolled] = useState(false);

  useMotionValueEvent(scrollY, "change", (latest) => {
    setIsScrolled(latest > 50);
  });

  return (
    <header className="fixed top-0 left-0 right-0 z-50 flex justify-center pt-4 px-4">
      <motion.nav
        className={cn(
          "flex items-center justify-between px-4 h-14 w-full max-w-[1200px]",
          "transition-all duration-300 ease-out",
          isScrolled
            ? "bg-white/95 dark:bg-black/95 backdrop-blur-md border border-black dark:border-white"
            : "bg-white dark:bg-black border border-transparent"
        )}
        initial={false}
        animate={{
          scale: isScrolled ? 0.85 : 1,
        }}
        transition={{
          duration: 0.3,
          ease: [0.32, 0.72, 0, 1],
        }}
        style={{
          transformOrigin: "center top",
        }}
      >
        <Link href="/" className="text-black dark:text-white">
          <div>
            {/* Mobile: Logo mark only */}
            <LogoMark size={24} className="md:hidden" />
            {/* Desktop: Full logo with text */}
            <LogoFull className="hidden md:flex" />
          </div>
        </Link>

        <div className="flex items-center gap-2">
          <ThemeToggle />
          <Link href="/sign-in">
            <Button variant="secondary" size="default">
              Login
            </Button>
          </Link>
        </div>
      </motion.nav>
    </header>
  );
}

import { ButtonHTMLAttributes, ReactNode } from "react";

interface ButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  children: ReactNode;
  variant?: "primary" | "solid" | "secondary" | "ghost";
  size?: "default" | "lg";
}

export function Button({
  children,
  variant = "primary",
  size = "default",
  className = "",
  ...props
}: ButtonProps) {
  const baseStyles =
    "inline-flex items-center justify-center font-medium transition-colors cursor-pointer focus:outline-none focus:ring-2 focus:ring-black focus:ring-offset-2";

  const variants = {
    primary:
      "bg-white dark:bg-black text-black dark:text-white border border-black dark:border-white hover:bg-zinc-100 dark:hover:bg-zinc-800 rounded-none",
    solid:
      "bg-black dark:bg-white text-white dark:text-black border border-black dark:border-white hover:bg-zinc-800 dark:hover:bg-zinc-100 rounded-none",
    secondary:
      "bg-transparent text-black dark:text-white border border-black dark:border-white hover:bg-zinc-100 dark:hover:bg-zinc-800 rounded-none",
    ghost:
      "bg-transparent text-black dark:text-white hover:bg-zinc-100 dark:hover:bg-zinc-800 border border-transparent hover:border-zinc-200 dark:hover:border-zinc-700 rounded-none",
  };

  const sizes = {
    default: "h-10 px-5 text-sm",
    lg: "h-12 px-8 text-base",
  };

  return (
    <button
      className={`${baseStyles} ${variants[variant]} ${sizes[size]} ${className}`}
      {...props}
    >
      {children}
    </button>
  );
}

