"use client";

import Image from "next/image";
import { cn } from "@/lib/utils";
import { Container } from "./ui/Container";
import { InfiniteSlider } from "./ui/InfiniteSlider";
import { PlusCorner } from "./ui/PlusCorner";

interface TeamCredentialsSectionProps {
  className?: string;
}

// Company and university logos
// logoDark is optional - used when the logo needs a different version for dark mode
// Order: DeepMind and Google are spaced apart so they're not visible simultaneously
const credentials = [
  { name: "DeepMind", logo: "/logos/deepmind.svg" },
  { name: "McKinsey", logo: "/logos/mckinsey.svg" },
  { name: "Bain", logo: "/logos/bain.svg" },
  { name: "Google", logo: "/logos/google.svg" },
  { name: "Boeing", logo: "/logos/boeing.svg" },
  { name: "Harvard", logo: "/logos/harvard.svg", logoDark: "/logos/harvard-white-text.svg" },
  { name: "Aalto", logo: "/logos/aalto.png", logoDark: "/logos/aalto-white.svg", className: "scale-150" },
];

export function TeamCredentialsSection({
  className,
}: TeamCredentialsSectionProps) {
  return (
    <section className={cn("py-12 md:py-16 bg-background", className)}>
      <Container>
        <div className="relative mx-auto max-w-3xl px-6 md:px-12">
          {/* Top border with plus corners - matching Hero pattern */}
          <div className="absolute top-0 left-0 right-0 border-t border-dashed border-zinc-200 dark:border-zinc-800" />
          <PlusCorner className="absolute -top-2.5 -left-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />
          <PlusCorner className="absolute -top-2.5 -right-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />

          {/* Label - positioned over the top border with background to mask the line */}
          <div className="absolute top-0 left-1/2 -translate-x-1/2 -translate-y-1/2">
            <span className="bg-background px-4 text-xs font-mono uppercase tracking-wider text-zinc-400 dark:text-zinc-500">
              Built by engineers from
            </span>
          </div>

          {/* Logo cloud */}
          <div className="overflow-hidden pt-8 pb-6 [mask-image:linear-gradient(to_right,transparent,black_10%,black_90%,transparent)]">
            <InfiniteSlider gap={64} reverse speed={5}>
              {credentials.map((cred) => (
                <div
                  key={cred.name}
                  className="flex items-center justify-center h-8 grayscale opacity-50 hover:opacity-100 hover:grayscale-0 transition-all duration-300"
                >
                  {/* Light mode logo */}
                  <Image
                    src={cred.logo}
                    alt={cred.name}
                    width={120}
                    height={32}
                    className={cn(
                      "h-6 md:h-8 w-auto object-contain",
                      cred.logoDark ? "dark:hidden" : "",
                      cred.className
                    )}
                  />
                  {/* Dark mode logo (if different) */}
                  {cred.logoDark && (
                    <Image
                      src={cred.logoDark}
                      alt={cred.name}
                      width={120}
                      height={32}
                      className={cn(
                        "h-6 md:h-8 w-auto object-contain hidden dark:block",
                        cred.className
                      )}
                    />
                  )}
                </div>
              ))}
            </InfiniteSlider>
          </div>

          {/* Bottom border with plus corners */}
          <div className="absolute bottom-0 left-0 right-0 border-t border-dashed border-zinc-200 dark:border-zinc-800" />
          <PlusCorner className="absolute -bottom-2.5 -left-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />
          <PlusCorner className="absolute -bottom-2.5 -right-2.5 w-5 h-5 text-zinc-400 dark:text-zinc-600" />
        </div>
      </Container>
    </section>
  );
}

"use client";

import { Container } from "./ui/Container";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/Accordion";

const faqs = [
  {
    question: "How does Entourage extract tasks from my communications?",
    answer:
      "Entourage uses AI to analyze your meetings, messages, and emails to identify action items, deadlines, and commitments. It understands context and prioritizes based on your projects.",
  },
  {
    question: "Is my data secure?",
    answer:
      "Yes. Your data is encrypted at rest and in transit. We never share your data with third parties, and you can delete your data at any time.",
  },
  {
    question: "Which integrations are supported?",
    answer:
      "We currently support Linear, Slack, Google Calendar, Gmail, Outlook, WhatsApp, Notion, Fireflies, Granola, and ChatGPT. More integrations are coming soon.",
  },
  {
    question: "Do I need to manually approve tasks?",
    answer:
      "Yes. Entourage suggests tasks but you remain in control. Review and approve suggestions before they sync to your task manager.",
  },
];

export function FAQ() {
  return (
    <section className="py-16 md:py-24 bg-background">
      <Container>
        <div className="text-center mb-12">
          <span className="text-xs font-mono uppercase tracking-wider text-zinc-400 dark:text-zinc-500">
            FAQ
          </span>
          <h2 className="mt-2 text-2xl md:text-3xl font-semibold text-black dark:text-white">
            Common questions
          </h2>
        </div>

        <div className="max-w-2xl mx-auto">
          <Accordion type="single" collapsible>
            {faqs.map((faq, i) => (
              <AccordionItem key={i} value={`item-${i}`}>
                <AccordionTrigger>{faq.question}</AccordionTrigger>
                <AccordionContent>{faq.answer}</AccordionContent>
              </AccordionItem>
            ))}
          </Accordion>
        </div>
      </Container>
    </section>
  );
}

"use client";

import Link from "next/link";
import { motion, useReducedMotion } from "framer-motion";
import { Container } from "./ui/Container";
import { Button } from "./ui/Button";

export function SecondaryCTA() {
  const prefersReducedMotion = useReducedMotion();

  if (prefersReducedMotion) {
    return (
      <section className="py-20 md:py-28">
        <Container>
          <div className="mx-auto max-w-xl text-center">
            <h2 className="text-2xl font-semibold tracking-tight text-black dark:text-white md:text-3xl">
              Ready to stop missing action items?
            </h2>

            <div className="mt-8">
              <Link href="/sign-up">
                <Button variant="primary" size="lg">
                  Join the Waitlist
                </Button>
              </Link>
            </div>

            <p className="mt-4 text-sm text-zinc-500 dark:text-zinc-400">
              We&apos;re onboarding users in batches. Get early access.
            </p>
          </div>
        </Container>
      </section>
    );
  }

  return (
    <section className="py-20 md:py-28">
      <Container>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, margin: "-100px" }}
          transition={{ duration: 0.12, ease: "easeOut" }}
          className="mx-auto max-w-xl text-center"
        >
          <h2 className="text-2xl font-semibold tracking-tight text-black dark:text-white md:text-3xl">
            Ready to stop missing action items?
          </h2>

          <motion.div
            initial={{ opacity: 0, y: 10 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.12, delay: 0.05, ease: "easeOut" }}
            className="mt-8"
          >
            <Link href="/sign-up">
              <Button variant="primary" size="lg">
                Join the Waitlist
              </Button>
            </Link>
          </motion.div>

          <motion.p
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.12, delay: 0.1 }}
            className="mt-4 text-sm text-zinc-500 dark:text-zinc-400"
          >
            We&apos;re onboarding users in batches. Get early access.
          </motion.p>
        </motion.div>
      </Container>
    </section>
  );
}

